mapper.select:
select distinct T.countTime, T.loadCount, T.loadPrincipal, T.loadAmount, T.firstLoadCount, T.firstLoadPrincipal, T.firstLoadAmount, T.againLoadCount, T.againLoadPrincipal, T.againLoadAmount from ( select DATE_FORMAT(p.time,'%Y-%m-%d') countTime, IFNULL(p1.loadCount,0) loadCount, IFNULL(p2.loadPrincipal,0) loadPrincipal, IFNULL(p3.loadAmount,0) loadAmount, IFNULL(p4.firstLoadCount,0) firstLoadCount, IFNULL(p5.firstLoadPrincipal,0) firstLoadPrincipal, IFNULL(p6.firstLoadAmount,0) firstLoadAmount, IFNULL(p7.againLoadCount,0) againLoadCount, IFNULL(p8.againLoadPrincipal,0) againLoadPrincipal, IFNULL(p9.againLoadAmount,0) againLoadAmount
from ( (SELECT COUNT(id) loadCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT borrow.id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id ) t2 GROUP BY time) UNION (SELECT sum(real_amount) loadPrincipal,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT borrow.real_amount,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id ) t2 GROUP BY time) UNION (SELECT sum(amount) loadAmount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT borrow.amount,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id ) t2 GROUP BY time) UNION
(SELECT COUNT(id) firstLoadCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT borrow.id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id and borrow.again = '10') t2 GROUP BY time) UNION (SELECT sum(real_amount) firstLoadPrincipal,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT borrow.real_amount,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id and borrow.again = '10') t2 GROUP BY time) UNION (SELECT sum(amount) firstLoadAmount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT borrow.amount,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id and borrow.again = '10') t2 GROUP BY time) UNION
(SELECT COUNT(id) againLoadCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT borrow.id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id and borrow.again = '20') t2 GROUP BY time) UNION (SELECT sum(real_amount) againLoadPrincipal,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT borrow.real_amount,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id and borrow.again = '20') t2 GROUP BY time) UNION (SELECT sum(amount) againLoadAmount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT borrow.amount,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id and borrow.again = '20') t2 GROUP BY time)
) p left join (SELECT COUNT(id) loadCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT borrow.id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id ) t2 GROUP BY time) p1 on p.time = p1.time left join (SELECT sum(real_amount) loadPrincipal,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT borrow.real_amount,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id ) t2 GROUP BY time) p2 on p.time = p2.time left join (SELECT sum(amount) loadAmount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT borrow.amount,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id ) t2 GROUP BY time) p3 on p.time = p3.time
left join (SELECT COUNT(id) firstLoadCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT borrow.id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id and borrow.again = '10') t2 GROUP BY time) p4 on p.time = p4.time left join (SELECT sum(real_amount) firstLoadPrincipal,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT borrow.real_amount,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id and borrow.again = '10') t2 GROUP BY time) p5 on p.time = p5.time left join (SELECT sum(amount) firstLoadAmount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT borrow.amount,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id and borrow.again = '10') t2 GROUP BY time) p6 on p.time = p6.time
left join (SELECT COUNT(t2.user_id) againLoadCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT distinct borrow.user_id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id and borrow.again = '20') t2 GROUP BY time) p7 on p.time = p7.time left join (SELECT sum(real_amount) againLoadPrincipal,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT borrow.real_amount,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id and borrow.again = '20') t2 GROUP BY time) p8 on p.time = p8.time left join (SELECT sum(amount) againLoadAmount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT borrow.amount,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id and borrow.again = '20') t2 GROUP BY time) p9 on p.time = p9.time
and DATE_FORMAT(p.time, '%Y-%m-%d') > #{startDate,jdbcType=VARCHAR}
and DATE_FORMAT(p.time, '%Y-%m-%d') <= #{endDate,jdbcType=VARCHAR}
) T ORDER BY T.countTime DESC
