mapper.select:
select distinct T.date, T.userRegist, T.authCount, T.contactCount, T.bankCount, T.phoneCount, T.borrowApplyCount, T.newBorrowCount, T.oldBorrowCount, T.loadCount, T.newLoadCount, T.oldLoadCount from ( select DATE_FORMAT(p.time,'%Y-%m-%d') date, IFNULL(p2.userRegist,0) userRegist, IFNULL(p3.authCount,0) authCount, IFNULL(p4.contactCount,0) contactCount, IFNULL(p5.bankCount,0) bankCount, IFNULL(p6.phoneCount,0) phoneCount, IFNULL(p7.borrowApplyCount,0) borrowApplyCount, IFNULL(p8.newBorrowCount,0) newBorrowCount, IFNULL(p9.oldBorrowCount,0) oldBorrowCount, IFNULL(p10.loadCount,0) loadCount, IFNULL(p11.newLoadCount,0) newLoadCount, IFNULL(p12.oldLoadCount,0) oldLoadCount
from (
(SELECT COUNT(id) userRegist,DATE_FORMAT(regist_time,'%Y-%m-%d') time from cl_user t2 GROUP BY time ) UNION (SELECT COUNT(id) authCount,DATE_FORMAT(regist_time,'%Y-%m-%d') time from (SELECT cu.id ,DATE_FORMAT(regist_time,'%Y-%m-%d') regist_time from cl_user cu left join cl_user_auth cua on cu.id = cua.user_id where cua.id_state = '30') t2 GROUP BY time )UNION (SELECT COUNT(id) contactCount,DATE_FORMAT(regist_time,'%Y-%m-%d') time from (SELECT cu.id ,DATE_FORMAT(regist_time,'%Y-%m-%d') regist_time from cl_user cu left join cl_user_auth cua on cu.id = cua.user_id where cua.contact_state = '30') t2 GROUP BY time ) UNION (SELECT COUNT(id) bankCount,DATE_FORMAT(regist_time,'%Y-%m-%d') time from (SELECT cu.id ,DATE_FORMAT(regist_time,'%Y-%m-%d') regist_time from cl_user cu left join cl_user_auth cua on cu.id = cua.user_id where cua.bank_card_state = '30') t2 GROUP BY time )UNION (SELECT COUNT(id) phoneCount,DATE_FORMAT(regist_time,'%Y-%m-%d') time from (SELECT cu.id ,DATE_FORMAT(regist_time,'%Y-%m-%d') regist_time from cl_user cu left join cl_user_auth cua on cu.id = cua.user_id where cua.phone_state = '30') t2 GROUP BY time ) UNION (SELECT COUNT(id) borrowApplyCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT id,DATE_FORMAT(create_time, '%Y-%m-%d') regist_time FROM cl_borrow ) t2 GROUP BY time ) UNION (SELECT COUNT(id) newBorrowCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT id,DATE_FORMAT(create_time, '%Y-%m-%d') regist_time FROM cl_borrow where again='10') t2 GROUP BY time ) UNION (SELECT COUNT(id) oldBorrowCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT id,DATE_FORMAT(create_time, '%Y-%m-%d') regist_time FROM cl_borrow where again='20') t2 GROUP BY time ) UNION
(SELECT COUNT(id) loadCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT borrow.id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id ) t2 GROUP BY time) UNION (SELECT COUNT(id) newLoadCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT borrow.id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id and borrow.again ='10') t2 GROUP BY time) UNION (SELECT COUNT(id) oldLoadCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT borrow.id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id and borrow.again ='20') t2 GROUP BY time)
) p left join (SELECT COUNT(id) userRegist,DATE_FORMAT(regist_time,'%Y-%m-%d') time from cl_user t2 GROUP BY time ) p2 on p.time = p2.time left join (SELECT COUNT(id) authCount,DATE_FORMAT(regist_time,'%Y-%m-%d') time from (SELECT cu.id ,DATE_FORMAT(regist_time,'%Y-%m-%d') regist_time from cl_user cu left join cl_user_auth cua on cu.id = cua.user_id where cua.id_state = '30') t2 GROUP BY time ) p3 on p.time = p3.time left join (SELECT COUNT(id) contactCount,DATE_FORMAT(regist_time,'%Y-%m-%d') time from (SELECT cu.id ,DATE_FORMAT(regist_time,'%Y-%m-%d') regist_time from cl_user cu left join cl_user_auth cua on cu.id = cua.user_id where cua.contact_state = '30') t2 GROUP BY time ) p4 on p.time = p4.time left join (SELECT COUNT(id) bankCount,DATE_FORMAT(regist_time,'%Y-%m-%d') time from (SELECT cu.id ,DATE_FORMAT(regist_time,'%Y-%m-%d') regist_time from cl_user cu left join cl_user_auth cua on cu.id = cua.user_id where cua.bank_card_state = '30') t2 GROUP BY time ) p5 on p.time = p5.time left join (SELECT COUNT(id) phoneCount,DATE_FORMAT(regist_time,'%Y-%m-%d') time from (SELECT cu.id ,DATE_FORMAT(regist_time,'%Y-%m-%d') regist_time from cl_user cu left join cl_user_auth cua on cu.id = cua.user_id where cua.phone_state = '30') t2 GROUP BY time ) p6 on p.time = p6.time
left join (SELECT COUNT(id) borrowApplyCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT id,DATE_FORMAT(create_time, '%Y-%m-%d') regist_time FROM cl_borrow ) t2 GROUP BY time ) p7 on p.time = p7.time left join (SELECT COUNT(id) newBorrowCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT id,DATE_FORMAT(create_time, '%Y-%m-%d') regist_time FROM cl_borrow where again='10') t2 GROUP BY time ) p8 on p.time = p8.time left join (SELECT COUNT(id) oldBorrowCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT id,DATE_FORMAT(create_time, '%Y-%m-%d') regist_time FROM cl_borrow where again='20') t2 GROUP BY time) p9 on p.time = p9.time
left join (SELECT COUNT(id) loadCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT borrow.id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id ) t2 GROUP BY time) p10 on p.time = p10.time left join (SELECT COUNT(id) newLoadCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT borrow.id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id and borrow.again ='10') t2 GROUP BY time) p11 on p.time = p11.time left join (SELECT COUNT(id) oldLoadCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT borrow.id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow ,cl_borrow_progress as progress where progress.state = '30' and borrow.id = progress.borrow_id and borrow.again ='20') t2 GROUP BY time) p12 on p.time = p12.time
and p.time >= #{startDate,jdbcType=VARCHAR}
and p.time <= #{endDate,jdbcType=VARCHAR}
) T ORDER BY T.date DESC

select distinct T.date, T.channelId, T.userRegister, T.borrowApplyCount, T.machineAuditPassCount, T.machineAuditNotPassCount, T.reviewPassCount, T.reviewNotPassCount, T.firstLoadCount, T.againLoadCount, T.overdueCount, T.firstOverdueCount
from ( select DATE_FORMAT(p.time,'%Y-%m-%d') date, IFNULL(p.channelId,0) channelId, IFNULL(p2.userRegister,0) userRegister, IFNULL(p3.borrowApplyCount,0) borrowApplyCount, IFNULL(p4.machineAuditPassCount,0) machineAuditPassCount, IFNULL(p5.machineAuditNotPassCount,0) machineAuditNotPassCount, IFNULL(p6.reviewPassCount,0) reviewPassCount, IFNULL(p7.reviewNotPassCount,0) reviewNotPassCount, IFNULL(p8.firstLoadCount,0) firstLoadCount, IFNULL(p9.againLoadCount,0) againLoadCount, IFNULL(p10.overdueCount,0) overdueCount, IFNULL(p11.firstOverdueCount,0) firstOverdueCount
from (
(SELECT channel.id as channelId,COUNT(channel.id) userRegister,DATE_FORMAT(regist_time,'%Y-%m-%d') time from cl_user t2 ,cl_channel as channel where channel.id = t2.channel_id GROUP BY time,channel.id ) UNION (SELECT t2.channelId as channelId, COUNT(t2.id) borrowApplyCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT channel.id as channelId,borrow.id,DATE_FORMAT(borrow.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow,cl_user usr,cl_channel as channel where borrow.user_id = usr.id and usr.channel_id = channel.id ) t2 GROUP BY time ,channelId) UNION (SELECT t2.channelId as channelId,COUNT(t2.id) machineAuditPassCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT channel.id as channelId ,borrow.id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_user usr ,cl_channel as channel ,cl_borrow as borrow ,cl_borrow_progress as progress where progress.state ='22' and borrow.id = progress.borrow_id and usr.id =borrow.user_id and usr.channel_id = channel.id) t2 GROUP BY time,channelId ) UNION (SELECT t2.channelId as channelId,COUNT(t2.id) machineAuditNotPassCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT channel.id as channelId ,borrow.id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_user usr ,cl_channel as channel ,cl_borrow as borrow ,cl_borrow_progress as progress where progress.state ='21' and borrow.id = progress.borrow_id and usr.id =borrow.user_id and usr.channel_id = channel.id) t2 GROUP BY time,channelId )UNION (SELECT t2.channelId as channelId,COUNT(t2.id) reviewPassCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT channel.id as channelId ,borrow.id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_user usr ,cl_channel as channel ,cl_borrow as borrow ,cl_borrow_progress as progress where progress.state ='26' and borrow.id = progress.borrow_id and usr.id =borrow.user_id and usr.channel_id = channel.id) t2 GROUP BY time,channelId ) UNION (SELECT t2.channelId as channelId,COUNT(t2.id) reviewNotPassCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT channel.id as channelId ,borrow.id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_user usr ,cl_channel as channel ,cl_borrow as borrow ,cl_borrow_progress as progress where progress.state ='27' and borrow.id = progress.borrow_id and usr.id =borrow.user_id and usr.channel_id = channel.id) t2 GROUP BY time,channelId ) UNION (SELECT t2.channelId as channelId,COUNT(t2.id) firstLoadCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT channel.id as channelId ,borrow.id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_user usr ,cl_channel as channel ,cl_borrow as borrow ,cl_borrow_progress as progress where progress.state ='30' and borrow.id = progress.borrow_id and usr.id =borrow.user_id and usr.channel_id = channel.id and borrow.again='10') t2 GROUP BY time,channelId ) UNION (SELECT t2.channelId as channelId,COUNT(t2.id) againLoadCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT channel.id as channelId ,borrow.id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_user usr ,cl_channel as channel ,cl_borrow as borrow ,cl_borrow_progress as progress where progress.state ='30' and borrow.id = progress.borrow_id and usr.id =borrow.user_id and usr.channel_id = channel.id and borrow.again='20') t2 GROUP BY time,channelId ) UNION (SELECT t2.channelId as channelId ,COUNT(t2.id) overdueCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT channel.id as channelId , borrow.id,DATE_FORMAT(pl.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow , cl_pay_log as pl ,cl_user as usr ,cl_channel as channel where pl.borrow_id = borrow.id and usr.id = borrow.user_id and usr.channel_id = channel.id and pl.type=10 and pl.state=40 ) t2 GROUP BY time,channelId ) UNION (SELECT t2.channelId as channelId ,COUNT(t2.id) firstOverdueCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT channel.id as channelId , borrow.id,DATE_FORMAT(pl.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow , cl_pay_log as pl ,cl_user as usr ,cl_channel as channel where pl.borrow_id = borrow.id and usr.id = borrow.user_id and usr.channel_id = channel.id and pl.type=10 and pl.state=40 and borrow.again='10') t2 GROUP BY time,channelId )
) p left join (SELECT channel.id as channelId,COUNT(channel.id) userRegister,DATE_FORMAT(regist_time,'%Y-%m-%d') time from cl_user t2 ,cl_channel as channel where channel.id = t2.channel_id GROUP BY time,channelId ) p2 on p.time = p2.time left join (SELECT t2.channelId as channelId, COUNT(t2.id) borrowApplyCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT channel.id as channelId,borrow.id,DATE_FORMAT(borrow.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow,cl_user usr,cl_channel as channel where borrow.user_id = usr.id and usr.channel_id = channel.id ) t2 GROUP BY time ,channelId) p3 on p.time = p3.time left join (SELECT t2.channelId as channelId,COUNT(t2.id) machineAuditPassCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT channel.id as channelId ,borrow.id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_user usr ,cl_channel as channel ,cl_borrow as borrow ,cl_borrow_progress as progress where progress.state ='22' and borrow.id = progress.borrow_id and usr.id =borrow.user_id and usr.channel_id = channel.id) t2 GROUP BY time,channelId ) p4 on p.time = p4.time left join (SELECT t2.channelId as channelId,COUNT(t2.id) machineAuditNotPassCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT channel.id as channelId ,borrow.id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_user usr ,cl_channel as channel ,cl_borrow as borrow ,cl_borrow_progress as progress where progress.state ='21' and borrow.id = progress.borrow_id and usr.id =borrow.user_id and usr.channel_id = channel.id) t2 GROUP BY time,channelId ) p5 on p.time = p5.time left join (SELECT t2.channelId as channelId,COUNT(t2.id) reviewPassCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT channel.id as channelId ,borrow.id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_user usr ,cl_channel as channel ,cl_borrow as borrow ,cl_borrow_progress as progress where progress.state ='26' and borrow.id = progress.borrow_id and usr.id =borrow.user_id and usr.channel_id = channel.id) t2 GROUP BY time,channelId ) p6 on p.time = p6.time left join (SELECT t2.channelId as channelId,COUNT(t2.id) reviewNotPassCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT channel.id as channelId ,borrow.id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_user usr ,cl_channel as channel ,cl_borrow as borrow ,cl_borrow_progress as progress where progress.state ='27' and borrow.id = progress.borrow_id and usr.id =borrow.user_id and usr.channel_id = channel.id) t2 GROUP BY time,channelId ) p7 on p.time = p7.time left join (SELECT t2.channelId as channelId,COUNT(t2.id) firstLoadCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT channel.id as channelId ,borrow.id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_user usr ,cl_channel as channel ,cl_borrow as borrow ,cl_borrow_progress as progress where progress.state ='30' and borrow.id = progress.borrow_id and usr.id =borrow.user_id and usr.channel_id = channel.id and borrow.again='10' ) t2 GROUP BY time,channelId ) p8 on p.time = p8.time left join (SELECT t2.channelId as channelId,COUNT(t2.id) againLoadCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT channel.id as channelId ,borrow.id,DATE_FORMAT(progress.create_time, '%Y-%m-%d') regist_time FROM cl_user usr ,cl_channel as channel ,cl_borrow as borrow ,cl_borrow_progress as progress where progress.state ='30' and borrow.id = progress.borrow_id and usr.id =borrow.user_id and usr.channel_id = channel.id and borrow.again='20') t2 GROUP BY time,channelId ) p9 on p.time = p9.time left join (SELECT t2.channelId as channelId ,COUNT(t2.id) overdueCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT channel.id as channelId , borrow.id,DATE_FORMAT(pl.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow , cl_pay_log as pl ,cl_user as usr ,cl_channel as channel where pl.borrow_id = borrow.id and usr.id = borrow.user_id and usr.channel_id = channel.id and pl.type=10 and pl.state=40 ) t2 GROUP BY time,channelId ) p10 on p.time = p10.time left join (SELECT t2.channelId as channelId ,COUNT(t2.id) firstOverdueCount,DATE_FORMAT(regist_time, '%Y-%m-%d') time FROM ( SELECT channel.id as channelId , borrow.id,DATE_FORMAT(pl.create_time, '%Y-%m-%d') regist_time FROM cl_borrow as borrow , cl_pay_log as pl ,cl_user as usr ,cl_channel as channel where pl.borrow_id = borrow.id and usr.id = borrow.user_id and usr.channel_id = channel.id and pl.type=10 and pl.state=40 and borrow.again='10') t2 GROUP BY time,channelId ) p11 on p.time = p11.time
and p.time >= #{startDate,jdbcType=VARCHAR}
and p.time <= #{endDate,jdbcType=VARCHAR}
) T ORDER BY T.date DESC
