mapper.select:
        select

        from ${tableName} 


                user_id = #{params.userId,jdbcType=BIGINT}


				and name like concat("%",#{params.name, jdbcType=VARCHAR},"%")


				and phone like concat("%",#{params.phone, jdbcType=VARCHAR},"%")

        select

        from ${tableName1} 


                user_id = #{params.userId,jdbcType=BIGINT}


				and name like concat("%",#{params.name, jdbcType=VARCHAR},"%")


				and phone like concat("%",#{params.phone, jdbcType=VARCHAR},"%")

SELECT contacts.*, IFNULL(totalCount, 0) as totalCount, IFNULL(sumDuration, 0) as sumDuration FROM ( SELECT * FROM ${contacts} WHERE 1 = 1
and user_id = #{params.userId,jdbcType=BIGINT}
and name like concat("%",#{params.name, jdbcType=VARCHAR},"%")
and phone like concat("%",#{params.phone, jdbcType=VARCHAR},"%")
GROUP BY phone ) AS contacts LEFT JOIN ( SELECT voice_to_number, COUNT(voice_to_number) AS totalCount, SUM(voice_duration) AS sumDuration FROM ${operator} WHERE 1 = 1
AND DATE_SUB(CURDATE(), INTERVAL #{params.month,jdbcType=INTEGER} MONTH) <= date(voice_date)
and user_id = #{params.userId,jdbcType=BIGINT}
GROUP BY voice_to_number ) AS operator ON RIGHT(contacts.phone,11) = RIGHT(operator.voice_to_number,11) ORDER BY totalCount DESC
limit #{params.topCount,jdbcType=INTEGER}

mapper.update:
update ${tableName1}


		total_count = #{totalCount,jdbcType=INTEGER},


		sum_duration = #{sumDuration,jdbcType=INTEGER}


where user_id = #{userId,jdbcType=INTEGER} and phone = #{phone,jdbcType=VARCHAR}
